INSERT INTO products (EAN, product_name, detail, price, brand, resolution, platform, refresh_rate, color, function, connection, accessoires) VALUES 
('4718487699124','HTC Vive', 'De Vive Consumer Edition wordt geleverd met de headset, twee draadloze controllers, een Vive Link Box, oordopjes en twee Vive-basisstations.', 599.00, 'HTC', '2160x1200', 'PC', '90Hz', 'zwart', 'Accelerometer, Camera, Gyroscoop, Verstelbare lenzen', 'HDMI, USB 2.0, USB 3.0', 'Controller(s), Headset bedraad, Kabels'),
       ('8158200201030','Oculus Rift Bundle (Rift + Touch)', 'Ga in één keer aan de slag met de Oculus Rift zoals het bedoeld is: met 2 touch controllers. Dit pakket omvat namelijk de Oculus Rift VR Bril, 2 Oculus Rift Touch Controllers en 6 gratis meegeleverde games. Ook beschik je over 2 Oculus sensoren die zowel de bewegingen van de headset als de precieze bewegingen van de controller in 3D volgen. De headset dompelt je onder in de virtuele wereld en de controllers zorgen dat je op natuurlijk wijze de interactie opzoekt met de virtuele objecten en vijanden die je tegenkomt. Zo ''gooi'' je granaten met dezelfde beweging als je dit in het echt zou doen. Op deze manier vergeet je dat je een controller in je handen hebt en ga je op in de game.', 449.00, 'Oculus', '2160x1200', 'PC', '90Hz', 'zilver', 'Accelerometer, Gyroscoop, Koptelefoon, Magnetometer', 'HDMI, USB 2.0, 3x USB 3.0', 'Afstandbediening, Controller(s)'),
       ('2750057115988','Sony PlayStation VR', 'Met de Sony PlayStation VR Worlds Pakket is jouw PS4 in één keer klaar voor VR. De hoofdrolspeler in het pakket is de Sony PlayStation VR bril, welke jou straks meeneemt in de diepgaande wereld van PlayStation VR games. Ook ontvang je de PlayStation Camera V2; deze camera volgt de bewegingen van de headset en vertaalt jouw hoofdbewegingen naar de game, zodat je de camera beweegt door in het rond te kijken. Met de meegeleverde VR Worlds game maak je meteen gebruik van je PlayStation VR en dompel je jezelf onder in een virtuele wereld.', 229.00, 'Sony', '1920x1080 (Full HD)', 'PlayStation 4', '90Hz', 'zwart', 'Accelerometer, Gyroscoop', 'HDMI, USB 2.0', 'Headset bedraad, Kabels'),
       ('0815820020004','Oculus Rift', 'Voor en achterkant bevat sensoren zodat er 360 graden positionele tracking is; Externe IR camera tracking sensor (met een bereik van 3,5m bij 1,5m). Dit kan later uitgebreid worden met meerdere sensoren;  Geïntegreerde over je oren audio headset met 3D positionele geluidsweergave; Geïntegreerde hoge kwaliteit microfoon; IPD (afstand tussen je ogen) kan worden geconfigureerd met een schuif en klik mechanisme. Range is 58mm tot 72mm;Connecties:HDMI+USB3.0 (lengte +/- 4 meter), USB 3.0 (Sensor), USB 2.0 (XBOX Wireless receiver).', 549.00, 'Oculus', '	2160x1200', 'PC', '90Hz', 'zwart', 'Accelerometer, Gyroscoop, Koptelefoon, Magnetometer','HDMI, USB 2.0, 3x USB 3.0', 'Afstandbediening, Controller(s), Kabels'),
       ('0192018001602','HP Windows Mixed Reality headset', 'Duik in de wereld van Mixed Reality waar de grenzen tussen echt en digitaal vervagen. De HP Mixed Reality Headset met controllers is ontworpen voor een geavanceerde weergave en optimaal comfort en biedt een geweldige computerervaring.', 399.00, 'HP', '2880x1440', 'PC', '90Hz', 'zwart', 'Accelerometer, Camera, Gyroscoop', '3.5mm, HDMI, USB 3.0', 'Controller(s), Headset bedraad, Kabels'),
       ('4718487708185','HTC Vive Pro', 'De HTC Vive Pro is de tweede generatie van de op gaming gerichte HTC Vive VR brillen, waarbij vooral kinderziektes zijn weggewerkt. Zo is de resolutie nog verder verhoogd naar 2880x1600 (1440p per oog). Dit maakt games gedetailleerder en samen met de verminderde light bleed zorgt dat voor een verbeterde immersie. Ook het draagcomfort is verbeterd, met een nieuw bevestigingssysteem met draaiknop. Hierdoor is de vive eenvoudiger zelf op te zetten of te verstellen en is het gewicht van de headset beter over je hoofd verdeeld. De ingebouwde microfoons en koptelefoons maken een externe headset overbodig, wat weer scheelt in het geknoei met kabels. De HTC Vive Pro is compatibel met alle Vive 1.0 accessoires, mocht je die al in huis hebben.', 879.00, 'HTC', '2880x1600', 'PC', '90Hz', 'Blauw, Zwart', 'Camera, Koptelefoon, Microfoon, Verstelbare lenzen', '3.5mm, USB 3.0 (Type-C)', NULL),
       ('8806088972039','Samsung Gear VR 4 + Gear VR Controller', 'Werkt met Samsung Galaxy Note 8, S8, S8 Plus of S7 Edge, S7, S6 edge Plus, S6', 114.95, 'Samsung', NULL, 'Smartphone', NULL, 'zwart', 'Accelerometer, Gyroscoop', 'Controller(s)', NULL),
       ('4015625616662','Medion Erazer X1000 Mixed Reality Headset', 'VR gamen zonder losse sensoren doe je voortaan met de Medion Erazer X1000 Mixed Reality VR Bril. Deze Virtual Reality bril volgt zelf de bewegingen van de bril, zonder dat je statieven en sensoren nodig hebt. Het is dus een kwestie van aansluiten op een geschikte windows 10 gaming pc en je duikt de wereld van VR in. Dankzij de 2 meegeleverde motion controllers interacter je direct met de virtuele objecten, zoals het gooien van een virtuele bal door de beweging na te bootsen of het bedienen van virtuele televisie. Even pauze? Dan klap je de bril omhoog om een gesprek te voeren en klap je hem terug naar beneden wanneer je verder speelt.', 449.00, 'Medion', '2880x1440', 'PC', '90Hz', 'Blauw, Zwart', 'Accelerometer, Camera, Gyroscoop, Microfoon', '3.5mm, HDMI, USB 2.0', 'Controller(s), Headset bedraad'),
       ('4718487692866','HTC Vive Business Edition', 'De Vive Business Edition wordt geleverd met de headset, twee draadloze controllers, een Vive Link Box, oordopjes en twee Vive-basisstations en Dedicated Business Edition customer support line.', 1389.00, 'HTC', '2160x1200', 'PC', '90Hz', 'zwart', 'Accelerometer, Camera, Gyroscoop, Verstelbare lenzen', 'HDMI, USB 2.0, USB 3.0', 'Controller(s), Headset bedraad, Kabels'),
